INSERT INTO `mybb_templates` (`tid`, `title`, `template`, `sid`, `version`, `status`, `dateline`) VALUES
(1, 'announcement', '<html>\n<head>\n<title>{$lang->forum_announcement}</title>\n{$headerinclude}\n<script type=\"text/javascript\">\nvar announcement_quickdelete_confirm = \"{$lang->announcement_quickdelete_confirm}\";\n</script>\n</head>\n<body>\n{$header}\n<table border=\"0\" cellspacing=\"{$theme[\'borderwidth\']}\" cellpadding=\"{$theme[\'tablespace\']}\" class=\"tborder tfixed clear no_bottom_border\">\n<tr>\n<td class=\"thead\"><strong>{$lang->forum_announcement}</strong></td>\n</tr>\n<tr><td id=\"posts_container\">\n	<div id=\"posts\">\n	{$announcement}\n	</div>\n</td></tr>\n</table>\n{$footer}\n</body>\n</html>', -2, '1820', '', 1719340773),
(2, 'announcement_edit', '<a href=\"modcp.php?action=edit_announcement&amp;aid={$post[\'aid\']}\" title=\"{$lang->announcement_edit}\" class=\"postbit_edit\"><span>{$lang->postbit_button_edit}</span></a>', -2, '1800', '', 1719340773),
(3, 'announcement_quickdelete', '<a href=\"modcp.php?action=delete_announcement&amp;aid={$post[\'aid\']}&amp;my_post_key={$mybb->post_code}\" onclick=\"MyBB.deleteAnnouncement(this); return false;\" class=\"postbit_qdelete\"><span>{$lang->postbit_button_qdelete}</span></a>', -2, '1800', '', 1719340773),
(4, 'calendar', '<html>\n<head>\n	<title>{$mybb->settings[\'bbname\']} - {$lang->calendar}</title>\n	{$headerinclude}\n</head>\n<body>\n	{$header}\n	<table border=\"0\" cellspacing=\"{$theme[\'borderwidth\']}\" cellpadding=\"{$theme[\'tablespace\']}\" class=\"tborder\">\n		<thead>\n			<tr>\n				<td class=\"thead\" colspan=\"8\">\n					<div class=\"float_right\">\n						{$prev_month_link}{$sep}{$next_month_link}\n					</div>\n					<div><strong>{$monthnames[$month]} {$year}</strong></div>\n				</td>\n			</tr>\n			<tr>\n				<td class=\"tcat\">&nbsp;</td>\n			{$weekday_headers}\n			</tr>\n		</thead>\n		<tbody>\n		{$calendar_rows}\n		</tbody>\n	</table>\n<br />\n<form action=\"calendar.php\" method=\"post\">\n	<table border=\"0\" cellspacing=\"{$theme[\'borderwidth\']}\" cellpadding=\"{$theme[\'tablespace\']}\" class=\"tborder\">\n		<tr>\n			<td class=\"trow1\">\n				<table width=\"100%\" cellspacing=\"0\" cellpadding=\"0\" border=\"0\">\n					<tr>\n						<td valign=\"top\">{$addevent}</td>\n						<td align=\"right\">\n						<span class=\"smalltext\"><strong>{$lang->jump_month}</strong></span>\n						<select name=\"month\">\n							<option value=\"{$month}\">{$monthnames[$month]}</option>\n							<option value=\"{$month}\">----------</option>\n							<option value=\"1\">{$lang->alt_month_1}</option>\n							<option value=\"2\">{$lang->alt_month_2}</option>\n							<option value=\"3\">{$lang->alt_month_3}</option>\n							<option value=\"4\">{$lang->alt_month_4}</option>\n							<option value=\"5\">{$lang->alt_month_5}</option>\n							<option value=\"6\">{$lang->alt_month_6}</option>\n							<option value=\"7\">{$lang->alt_month_7}</option>\n							<option value=\"8\">{$lang->alt_month_8}</option>\n							<option value=\"9\">{$lang->alt_month_9}</option>\n							<option value=\"10\">{$lang->alt_month_10}</option>\n							<option value=\"11\">{$lang->alt_month_11}</option>\n							<option value=\"12\">{$lang->alt_month_12}</option>\n						</select>\n						<select name=\"year\">\n							<option value=\"{$year}\">{$year}</option>\n							<option value=\"{$year}\">----------</option>\n							{$yearsel}\n						</select>\n						{$gobutton}\n						<br />\n						{$calendar_jump}\n						</td>\n					</tr>\n				</table>\n			</td>\n		</tr>\n		</table>\n	</form>\n{$footer}\n</body>\n</html>', -2, '1813', '', 1719340773),
