n	document.addEventListener(\"DOMContentLoaded\", function() {\r\n      const iconElements = document.querySelectorAll(\".expander\");\r\n\r\n      iconElements.forEach(function(iconElement) {\r\n        iconElement.addEventListener(\"click\", function(event) {\r\n          const theadElement = iconElement.closest(\".thead\");\r\n\r\n          iconElement.classList.toggle(\"cp-minus\");\r\n          iconElement.classList.toggle(\"cp-plus\");\r\n          theadElement.classList.toggle(\"hide\");\r\n\r\n          // Prevent the event from propagating to other elements\r\n          event.stopPropagation();\r\n        });\r\n      });\r\n    });\r\n  </script>\r\n\r\n<!--- Scrollbar --->\r\n<script>\r\n        document.addEventListener(\'DOMContentLoaded\', function() {\r\n            const updateScrollbarRadius = () => {\r\n                const scrollTop = window.scrollY;\r\n                const scrollHeight = document.documentElement.scrollHeight;\r\n                const clientHeight = window.innerHeight;\r\n\r\n                let radius = \'6px\'; // Default border-radius\r\n\r\n                if (scrollTop === 0) {\r\n                    // At the top of the page\r\n                    radius = \'0 0 6px 6px\'; // Bottom corners rounded\r\n                } else if (scrollTop + clientHeight >= scrollHeight) {\r\n                    // At the bottom of the page\r\n                    radius = \'6px 6px 0 0\'; // Top corners rounded\r\n                }\r\n\r\n                // Apply the border-radius to the scrollbar thumb\r\n                document.documentElement.style.setProperty(\'--scrollbar-radius\', radius);\r\n            };\r\n\r\n            // Initial call\r\n            updateScrollbarRadius();\r\n\r\n            // Update on scroll\r\n            window.addEventListener(\'scroll\', updateScrollbarRadius);\r\n        });\r\n    </script>', 4, '1838', '', 1722760224),
