(1902, 'search_results_posts', '<html>\r\n<head>\r\n<title>{$mybb->settings[\'bbname\']} - {$lang->search_results}</title>\r\n{$headerinclude}\r\n</head>\r\n<body>\r\n{$header}\r\n<table width=\"100%\" align=\"center\" border=\"0\">\r\n	<tr>\r\n		<td align=\"right\" valign=\"top\">{$multipage}</td>\r\n	</tr>\r\n</table>\r\n<table border=\"0\" cellspacing=\"{$theme[\'borderwidth\']}\" cellpadding=\"{$theme[\'tablespace\']}\" class=\"tborder\">\r\n	<tr>\r\n		<td colspan=\"9\" class=\"thead\">\r\n			<strong>{$lang->search_results}</strong>\r\n		</td>\r\n	</tr>\r\n	<tr>\r\n		<td class=\"tcat\" align=\"center\" colspan=\"3\"><span class=\"smalltext\"><strong><a href=\"{$sorturl}&amp;sortby=subject&amp;order=asc\">{$lang->post}</a> {$orderarrow[\'subject\']}</strong></span></td>\r\n		<td class=\"tcat hide_mobile\" align=\"center\"><span class=\"smalltext\"><strong><a href=\"{$sorturl}&amp;sortby=starter&amp;order=asc\">{$lang->author}</a> {$orderarrow[\'starter\']}</strong></span></td>\r\n		<td class=\"tcat hide_mobile\" align=\"center\"><span class=\"smalltext\"><strong><a href=\"{$sorturl}&amp;sortby=forum&amp;order=asc\">{$lang->forum}</a> {$orderarrow[\'forum\']}</strong></span></td>\r\n		<td class=\"tcat hide_mobile\" align=\"center\"><span class=\"smalltext\"><strong><a href=\"{$sorturl}&amp;sortby=replies&amp;order=desc\">{$lang->replies}</a> {$orderarrow[\'replies\']}</strong></span></td>\r\n		<td class=\"tcat hide_mobile\" align=\"center\"><span class=\"smalltext\"><strong><a href=\"{$sorturl}&amp;sortby=views&amp;order=desc\">{$lang->views}</a></strong> {$orderarrow[\'views\']}</span></td>\r\n		<td class=\"tcat hide_mobile\" align=\"center\"><span class=\"smalltext\"><strong><a href=\"{$sorturl}&amp;sortby=dateline&amp;order=desc\">{$lang->posted}</a> {$orderarrow[\'dateline\']}</strong></span></td>\r\n		{$inlinemodcol}\r\n	</tr>\r\n	{$selectall}\r\n	{$results}\r\n</table>\r\n<table width=\"100%\" align=\"center\" border=\"0\">\r\n	<tr>\r\n		<td align=\"left\" v